import { CompressedStreamWriter, ChecksumCalculator } from '../src/compression-writer';


describe('CompressedStreamWriter instance creation and destroy method testing', () => {
    let compressedWriter: CompressedStreamWriter;
    it('CompressedStreamWriter instance intialization', () => {
        compressedWriter = new CompressedStreamWriter();
        expect('').toBe('');
    });
    it('destroy method testing', () => {
        compressedWriter = new CompressedStreamWriter();
        compressedWriter.destroy();
        expect(compressedWriter.compressedData).toBe(undefined);
    });
});
describe('CompressedStreamWriter with write method testing', () => {
    let compressedWriter: CompressedStreamWriter;
    beforeEach((): void => {
        compressedWriter = new CompressedStreamWriter();
    });
    afterEach((): void => {
        compressedWriter.destroy();
        compressedWriter = undefined;
    });
    it('write with data as null', () => {
        expect(() => { compressedWriter.write(null, 0, 12) }).toThrowError();
    });
    it('write with data as undefined', () => {
        expect(() => { compressedWriter.write(undefined, 0, 12) }).toThrowError();
    });
    it('write with negative offset value', () => {
        expect(() => { compressedWriter.write(new Uint8Array(2), -1, 0) }).toThrow(new Error('ArgumentOutOfRangeException: Offset or length is incorrect'));
    });
    it('write with invalid offset', () => {
        expect(() => { compressedWriter.write(new Uint8Array(2), 10, 5) }).toThrow(new Error('ArgumentOutOfRangeException: Offset or length is incorrect'));
    });
    it('write with invalid offset and length', () => {
        expect(() => { compressedWriter.write(new Uint8Array(2), 2, 5) }).toThrow(new Error('ArgumentOutOfRangeException: Offset or length is incorrect'));
    });
    it('write with valid offset and  negative length', () => {
        expect(() => { compressedWriter.write(new Uint8Array(2), 2, -1) }).toThrowError();
    });
    it('write method with valid string testing', () => {
        let s: string = '';
        for (let i: number = 0; i < 70000; i++) {
            s += 'c';
        }
        let buf: ArrayBuffer = new ArrayBuffer(s.length);
        let bufView = new Uint8Array(buf);
        for (let i: number = 0; i < s.length; i++) {
            bufView[i] = s.charCodeAt(i);
        }
        compressedWriter.write(bufView, 0, s.length);
        compressedWriter.destroy();
        expect(compressedWriter.compressedData).toBe(undefined);
    });
    it('Test large file', () => {
        let s = ''
        let buf: ArrayBuffer = new ArrayBuffer(s.length);
        let bufView = new Uint8Array(buf);
        for (let i: number = 0; i < s.length; i++) {
            bufView[i] = s.charCodeAt(i);
        }
        compressedWriter.write(bufView, 0, s.length);
        compressedWriter.destroy();
        expect(compressedWriter.compressedData).toBe(undefined);
    });
});
