describe('Zip Large size File', () => {
    it('Pass large size file as input', (done) => {
        let archiveItem: ZipArchiveItem;
        let archive: ZipArchive;
        let httpRequest = new XMLHttpRequest();
        httpRequest.open('POST', '../base/spec/test.txt', true);
        httpRequest.responseType = 'blob';
        httpRequest.onreadystatechange = function () {
            if (httpRequest.readyState == 4) {
                if (httpRequest.status === 200 || httpRequest.status === 304) {
                    archive = new ZipArchive();
                    archiveItem = new ZipArchiveItem(httpRequest.response, 'sample.txt');
                    archive.addItem(archiveItem);
                    archive.saveAsBlob().then(() => {
                        archive.destroy();
                    });
                }
            }
        }
        httpRequest.send();
        setTimeout(function () {
            expect('').toBe('');
            done();
        }, 800);
    })
})
describe('Save as blob testing', () => {
    let archive: ZipArchive;
    let archiveItem: ZipArchiveItem;
    beforeEach((): void => {
        archive = new ZipArchive();
        jasmine.DEFAULT_TIMEOUT_INTERVAL = 500;
    });
    afterEach((): void => {
        archive.destroy();
        archive = undefined;
        if (archiveItem !== undefined) {
            archiveItem.destroy();
        }
        archiveItem = undefined;
    });
    it('public method testing', (done) => {
        archive.addDirectory('sample');
        archive.compressionLevel = 'NoCompression';
        archiveItem = new ZipArchiveItem(new Blob(['compression']), 'sample/textFile.txt');
        archive.addItem(archiveItem);
        archive.saveAsBlob();
        setTimeout(() => {
            expect('').toBe('');
            done();
        }, 50);
    });
    it('text file input as empty string ', () => {
        archiveItem = new ZipArchiveItem(new Blob(['']), 'textFile.txt');
        archive.compressionLevel = 'NoCompression';
        archive.addItem(archiveItem);
        archive.saveAsBlob();
        expect(archive.compressionLevel).toBe('NoCompression');
    }, 100);
    it('Blob with valid string', (done) => {
        let archiveIns = new ZipArchive();
        archiveIns.addDirectory('sample/');
        archiveIns.compressionLevel = 'NoCompression';
        let archiveItemCol = new ZipArchiveItem(new Blob(['document']), 'sample//textFile.txt');
        let archiveItem1 = new ZipArchiveItem(new Blob(['document']), 'sample//textFile1.txt');
        archiveIns.addItem(archiveItemCol);
        archiveIns.addItem(archiveItem1);
        archiveIns.saveAsBlob().then((blob) => {
            expect(blob).toBeDefined();
        });
        setTimeout(() => {
            done();
        }, 300);
    });
});